mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, c.name as category_name from dish as d left join category as c on d.category_id = c.id
and d.name like concat('%',#{name},'%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.update_time desc

select * from dish where status = 1
and category_id = #{categoryId}
order by update_time desc

select d.*, df.id as flavor_id, df.name as flavor_name, df.value as flavor_value, df.dish_id as dish_id from dish as d left join dish_flavor as df on d.id = df.dish_id
and d.category_id = #{categoryId}
and d.status = #{status}
and d.name like concat('%',#{name},'%')
order by d.update_time desc
