mapper.select:
select o.*, od.id as order_detail_id, od.name as order_detail_name, od.image, od.dish_id, od.setmeal_id,
       od.dish_flavor, od.number as order_detail_number, od.amount as order_detail_amount
from orders as o
left join order_detail as od on o.id = od.order_id


    and o.user_id = #{userId}


    and o.number like concat('%',#{number},'%')


    and o.phone like concat('%',#{phone},'%')


    and o.status = #{status}


    and o.order_time between #{beginTime} and #{endTime}
